// update Gains
   globGain = globGain + (globGainCmd-globGain)/100;
   elevGain = elevGain + (elevGainCmd - elevGain)/100;

   error = error * globGain * elevGain;

   if (filterNum == 0)
       position = IIR[1].clear();
